package com.zptioning.code.chapter08;import java.util.concurrent.CyclicBarrier;import java.util.concurrent.TimeUnit;public class CyclicBarrierTest {    static CyclicBarrier c = new CyclicBarrier(3);    public static void main(String[] args) {        new Thread(new Runnable() {            @Override            public void run() {                try {                    TimeUnit.SECONDS.sleep(5);                    c.await();                } catch (Exception e) {                }                System.out.println(1);            }        }).start();        System.out.println("wo ca lei!");        try {            c.await();        } catch (Exception e) {        }        System.out.println(2);    }}